import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async () => {
        try {
            // Fetch the CSRF token from Django
            const csrfResponse = await axios.get('http://127.0.0.1:8000/api/csrf/');
            const csrfToken = csrfResponse.data.csrfToken;

            // Include the CSRF token in the login request
            const response = await axios.post('http://127.0.0.1:8000/api/login/', {
                name,  // Use 'name' instead of 'username'
                password,
            }, {
                headers: {
                    'X-CSRFToken': csrfToken,
                },
            });
            if (response.status === 200) {
                localStorage.setItem('username', name); // Store the username in localStorage
                navigate('/leaderboard');  // Redirect to the leaderboard page
            }
        } catch (error) {
            alert('Login failed!');
        }
    };

    return (
        <div style={{ textAlign: 'center', marginTop: '50px' }}>
            <h2>Login</h2>
            <div>
                <input
                    type="text"
                    placeholder="Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    style={{ margin: '10px', padding: '10px', fontSize: '16px' }}
                />
                <br />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    style={{ margin: '10px', padding: '10px', fontSize: '16px' }}
                />
                <br />
                <button
                    onClick={handleLogin}
                    style={{ margin: '10px', padding: '10px 20px', fontSize: '16px' }}
                >
                    Login
                </button>
                <br />
                <button
                    onClick={() => navigate('/')}
                    style={{ margin: '10px', padding: '10px 20px', fontSize: '16px' }}
                >
                    Back to Home
                </button>
            </div>
        </div>
    );
}

export default Login;
