import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Login.css'; // Import the CSS file

function Login() {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async () => {
        try {
            // Fetch the CSRF token from Django
            const csrfResponse = await axios.get('http://127.0.0.1:8000/api/csrf/');
            const csrfToken = csrfResponse.data.csrfToken;

            // Include the CSRF token in the login request
            const response = await axios.post('http://127.0.0.1:8000/api/login/', {
                name,  // Use 'name' instead of 'username'
                password,
            }, {
                headers: {
                    'X-CSRFToken': csrfToken,
                },
            });
            if (response.status === 200) {
                localStorage.setItem('username', name); // Store the username in localStorage
                navigate('/leaderboard');  // Redirect to the leaderboard page
            }
        } catch (error) {
            alert('Login failed!');
        }
    };

    return (
        <div className="login-container">
            {/* Video background */}
            <video autoPlay loop muted className="video-background">
                <source src="./assets/Abstract moving blue background.mp4" type="video/mp4" />
                Your browser does not support the video tag.
            </video>
            {/* Form container */}
            <div className="login-form">
                <h2 className="login-heading">Login</h2>
                <div className="login-input-group">
                    <input
                        type="text"
                        placeholder="Name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        className="login-input"
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        className="login-input"
                    />
                    <button onClick={handleLogin} className="login-button">
                        Login
                    </button>
                    <button onClick={() => navigate('/')} className="login-button secondary">
                        Back to Home
                    </button>
                </div>
            </div>
        </div>
    );
}

export default Login;
