import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Leaderboard() {
    const [users, setUsers] = useState([]);
    const [isAdmin, setIsAdmin] = useState(false);
    const [showForm, setShowForm] = useState(false);
    const [selectedUser, setSelectedUser] = useState('');
    const [pointsToAdd, setPointsToAdd] = useState(0);

    useEffect(() => {
        // Fetch leaderboard data
        axios.get('http://127.0.0.1:8000/api/leaderboard/')
            .then(response => {
                setUsers(response.data);
            })
            .catch(error => {
                console.error('Error fetching leaderboard data:', error);
            });

        // Check if the logged-in user is the admin
        const loggedInUser = localStorage.getItem('username');
        if (loggedInUser === 'cscadmin') {
            setIsAdmin(true);
        }
    }, []);

    const handleAddPoints = async () => {
        try {
            const response = await axios.post(`http://127.0.0.1:8000/api/update-points/${selectedUser}/`, {
                points: pointsToAdd,
            });
            if (response.status === 200) {
                // Refresh the leaderboard after updating points
                const updatedResponse = await axios.get('http://127.0.0.1:8000/api/leaderboard/');
                setUsers(updatedResponse.data);
                setShowForm(false); // Close the pop-up form
            }
        } catch (error) {
            console.error('Error updating points:', error);
        }
    };

    const handleDeleteUser = async (userId) => {
        const confirmDelete = window.confirm('Are you sure you want to delete this user?');
        if (confirmDelete) {
            try {
                const response = await axios.delete(`http://127.0.0.1:8000/api/delete-user/${userId}/`);
                if (response.status === 200) {
                    // Refresh the leaderboard after deleting the user
                    const updatedResponse = await axios.get('http://127.0.0.1:8000/api/leaderboard/');
                    setUsers(updatedResponse.data);
                }
            } catch (error) {
                console.error('Error deleting user:', error);
            }
        }
    };

    return (
        <div>
            <h1>Leaderboard</h1>
            {isAdmin && (
                <button
                    onClick={() => setShowForm(true)}
                    style={{ margin: '10px', padding: '10px 20px', fontSize: '16px' }}
                >
                    Add Points
                </button>
            )}
            {showForm && (
                <div style={{
                    position: 'fixed',
                    top: '50%',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                    backgroundColor: 'white',
                    padding: '20px',
                    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
                    zIndex: 1000,
                }}>
                    <h3>Add Points</h3>
                    <select
                        value={selectedUser}
                        onChange={(e) => setSelectedUser(e.target.value)}
                        style={{ margin: '10px', padding: '10px', fontSize: '16px' }}
                    >
                        <option value="">Select a user</option>
                        {users.map(user => (
                            <option key={user.id} value={user.id}>{user.name}</option>
                        ))}
                    </select>
                    <br />
                    <input
                        type="number"
                        placeholder="Points to add"
                        value={pointsToAdd}
                        onChange={(e) => setPointsToAdd(parseInt(e.target.value))}
                        style={{ margin: '10px', padding: '10px', fontSize: '16px' }}
                    />
                    <br />
                    <button
                        onClick={handleAddPoints}
                        style={{ margin: '10px', padding: '10px 20px', fontSize: '16px' }}
                    >
                        Submit
                    </button>
                    <button
                        onClick={() => setShowForm(false)}
                        style={{ margin: '10px', padding: '10px 20px', fontSize: '16px' }}
                    >
                        Cancel
                    </button>
                </div>
            )}
            <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                {users.map((user, index) => (
                    <div key={user.id} style={{ border: '1px solid #ccc', padding: '10px', margin: '10px', width: '200px', position: 'relative' }}>
                        {isAdmin && (
                            <button
                                onClick={() => handleDeleteUser(user.id)}
                                style={{
                                    position: 'absolute',
                                    top: '5px',
                                    right: '5px',
                                    backgroundColor: 'red',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '50%',
                                    width: '25px',
                                    height: '25px',
                                    cursor: 'pointer',
                                }}
                            >
                                X
                            </button>
                        )}
                        <img
                            src={`http://127.0.0.1:8000${user.picture}`}
                            alt={user.name}
                            style={{ width: '100%', height: 'auto' }}
                        />
                        <h3>{user.name}</h3>
                        <p>Points: {user.points}</p>
                        {index < 10 && (  // Display "Qualified for the Conference" for the top 10 users
                            <p style={{ color: 'green', fontWeight: 'bold' }}>Qualified for the Conference</p>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Leaderboard;
