import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection
import './Leaderboard.css'; // Import the CSS file

function Leaderboard() {
    const [users, setUsers] = useState([]);
    const [isAdmin, setIsAdmin] = useState(false);
    const [showForm, setShowForm] = useState(false);
    const [selectedUser, setSelectedUser] = useState('');
    const [pointsToAdd, setPointsToAdd] = useState(0);
    const navigate = useNavigate(); // Initialize useNavigate

    useEffect(() => {
        // Fetch leaderboard data
        axios.get('http://127.0.0.1:8000/api/leaderboard/')
            .then(response => {
                setUsers(response.data);
            })
            .catch(error => {
                console.error('Error fetching leaderboard data:', error);
            });

        // Check if the logged-in user is the admin
        const loggedInUser = localStorage.getItem('username');
        if (loggedInUser === 'cscadmin') {
            setIsAdmin(true);
        }
    }, []);

    // Logout function
    const handleLogout = () => {
        localStorage.removeItem('username'); // Clear the username from localStorage
        navigate('/login'); // Redirect to the login page
    };

    const handleAddPoints = async () => {
        try {
            const response = await axios.post(`http://127.0.0.1:8000/api/update-points/${selectedUser}/`, {
                points: pointsToAdd,
            });
            if (response.status === 200) {
                // Refresh the leaderboard after updating points
                const updatedResponse = await axios.get('http://127.0.0.1:8000/api/leaderboard/');
                setUsers(updatedResponse.data);
                setShowForm(false); // Close the pop-up form
            }
        } catch (error) {
            console.error('Error updating points:', error);
        }
    };

    const handleDeleteUser = async (userId) => {
        const confirmDelete = window.confirm('Are you sure you want to delete this user?');
        if (confirmDelete) {
            try {
                const response = await axios.delete(`http://127.0.0.1:8000/api/delete-user/${userId}/`);
                if (response.status === 200) {
                    // Refresh the leaderboard after deleting the user
                    const updatedResponse = await axios.get('http://127.0.0.1:8000/api/leaderboard/');
                    setUsers(updatedResponse.data);
                }
            } catch (error) {
                console.error('Error deleting user:', error);
            }
        }
    };

    return (
        <div className="leaderboard-container">
            {/* Background animation */}
            <div className="background-animation"></div>

            {/* Logout button */}
            <button onClick={handleLogout} className="logout-button">
                Logout
            </button>

            {/* Leaderboard content */}
            <h1 className="leaderboard-title">Leaderboard</h1>
            {isAdmin && (
                <button
                    onClick={() => setShowForm(true)}
                    className="add-points-button"
                >
                    Add Points
                </button>
            )}
            {showForm && (
                <div className="points-form">
                    <h3>Add Points</h3>
                    <select
                        value={selectedUser}
                        onChange={(e) => setSelectedUser(e.target.value)}
                        className="form-select"
                    >
                        <option value="">Select a user</option>
                        {users.map(user => (
                            <option key={user.id} value={user.id}>{user.name}</option>
                        ))}
                    </select>
                    <br />
                    <input
                        type="number"
                        placeholder="Points to add"
                        value={pointsToAdd}
                        onChange={(e) => setPointsToAdd(parseInt(e.target.value))}
                        className="form-input"
                    />
                    <br />
                    <button
                        onClick={handleAddPoints}
                        className="form-button"
                    >
                        Submit
                    </button>
                    <button
                        onClick={() => setShowForm(false)}
                        className="form-button cancel"
                    >
                        Cancel
                    </button>
                </div>
            )}
            <div className="leaderboard-grid">
                {users.map((user, index) => (
                    <div key={user.id} className="user-card">
                        {isAdmin && (
                            <button
                                onClick={() => handleDeleteUser(user.id)}
                                className="delete-button"
                            >
                                X
                            </button>
                        )}
                        {/* Crown for 1st place */}
                        {index === 0 && (
                            <div className="crown-icon">üëë</div>
                        )}
                        {/* Medal for 2nd place */}
                        {index === 1 && (
                            <div className="medal-icon silver">ü•à</div>
                        )}
                        {/* Medal for 3rd place */}
                        {index === 2 && (
                            <div className="medal-icon bronze">ü•â</div>
                        )}
                        <img
                            src={`http://127.0.0.1:8000${user.picture}`}
                            alt={user.name}
                            className="user-image"
                        />
                        <h3 className="user-name">{user.name}</h3>
                        <p className="user-points"><span className='points'>{user.points}</span> Points</p>
                        {index < 10 && (  // Display "Qualified for the Conference" for the top 10 users
                            <p className="qualified-text">üèÜ Qualified for the Conference</p>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Leaderboard;
