import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Leaderboard() {
    const [users, setUsers] = useState([]);
    const [isAdmin, setIsAdmin] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch leaderboard data
        axios.get('http://127.0.0.1:8000/api/leaderboard/')
            .then(response => {
                setUsers(response.data);
            })
            .catch(error => {
                console.error('Error fetching leaderboard data:', error);
            });

        // Check if the logged-in user is the admin
        const loggedInUser = localStorage.getItem('username'); // Assuming you store the username in localStorage
        if (loggedInUser === 'aymenox') {
            setIsAdmin(true);
        }
    }, []);

    const handleAddPoints = (user_id, points) => {
        axios.post(`http://127.0.0.1:8000/api/update-points/${user_id}/`, { points })
            .then(response => {
                // Refresh the leaderboard after updating points
                axios.get('http://127.0.0.1:8000/api/leaderboard/')
                    .then(response => {
                        setUsers(response.data);
                    })
                    .catch(error => {
                        console.error('Error fetching leaderboard data:', error);
                    });
            })
            .catch(error => {
                console.error('Error updating points:', error);
            });
    };

    return (
        <div>
            <h1>Leaderboard</h1>
            <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                {users.map(user => (
                    <div key={user.id} style={{ border: '1px solid #ccc', padding: '10px', margin: '10px', width: '200px' }}>
                        <img
                            src={`http://127.0.0.1:8000${user.picture}`}
                            alt={user.name}
                            style={{ width: '100%', height: 'auto' }}
                        />
                        <h3>{user.name}</h3>
                        <p>Points: {user.points}</p>
                        {isAdmin && (
                            <div>
                                <input
                                    type="number"
                                    placeholder="Add Points"
                                    onChange={(e) => handleAddPoints(user.id, e.target.value)}
                                    style={{ margin: '10px', padding: '5px' }}
                                />
                            </div>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Leaderboard;
