import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Leaderboard() {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        fetchLeaderboard();
    }, []);

    const fetchLeaderboard = () => {
        axios.get('http://127.0.0.1:8000/api/leaderboard/')
            .then(response => {
                setUsers(response.data);
            })
            .catch(error => {
                console.error('Error fetching leaderboard data:', error);
            });
    };

    const handleUpdatePoints = (user_id, points) => {
        axios.post(`http://127.0.0.1:8000/api/update-points/${user_id}/`, { points })
            .then(response => {
                fetchLeaderboard();  // Refresh the leaderboard after updating points
            })
            .catch(error => {
                console.error('Error updating points:', error);
            });
    };

    return (
        <div>
            <h1>Leaderboard</h1>
            <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                {users.map(user => (
                    <div key={user.id} style={{ border: '1px solid #ccc', padding: '10px', margin: '10px', width: '200px' }}>
                        <img
                            src={`http://127.0.0.1:8000${user.picture}`}
                            alt={user.name}
                            style={{ width: '100%', height: 'auto' }}
                        />
                        <h3>{user.name}</h3>
                        <p>Points: {user.points}</p>
                        <input
                            type="number"
                            placeholder="New Points"
                            onChange={(e) => handleUpdatePoints(user.id, e.target.value)}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Leaderboard;
